def triangular(k):
    return k * (k + 1) // 2

def hexagonal(m):
    return m * (2 * m - 1)

# Find the first 7 numbers that are both triangular and hexagonal
intersection = []
k = m = 1
tri = triangular(k)
hex_ = hexagonal(m)
while len(intersection) < 7:
    if tri == hex_:
        intersection.append(tri)
        k += 1
        m += 1
        tri = triangular(k)
        hex_ = hexagonal(m)
    elif tri < hex_:
        k += 1
        tri = triangular(k)
    else:
        m += 1
        hex_ = hexagonal(m)
print(intersection[-1])  # => 91

