def fibonacci_sequence(max_value):
    sequence = [1, 2]
    while True:
        next_value = sequence[-1] + sequence[-2]
        if next_value > max_value:
            break
        sequence.append(next_value)
    return sequence

def sum_even_terms(sequence):
    even_terms = [num for num in sequence if num % 2 == 0]
    return sum(even_terms)

max_value = 4000000
sequence = fibonacci_sequence(max_value)
answer = sum_even_terms(sequence)

# Return the answer
answer
