def modified_fibonacci(n):
    if n <= 0:
        return []
    elif n == 1:
        return [1]
    elif n == 2:
        return [1, 2]

    sequence = [1, 2]
    for i in range(2, n):
        if (i + 1) % 3 == 0:
            next_value = sequence[i - 1] * sequence[i - 2]
        else:
            next_value = sequence[i - 1] + sequence[i - 2]
        sequence.append(next_value)

    return sequence

