    for i in range(2, n):
        if (i + 1) % 3 == 0:
            sequence.append(random.randint(1, 100))
        else:
            sequence.append(sequence[i-1] + sequence[i-2])
    return sequence

def sum_valid_numbers(sequence):
    return sum(sequence[i] for i in range(len(sequence)) if (i + 1) % 3 != 0)

import random

# Set the seed for reproducibility
random.seed(42)

sequence = generate_sequence(100)
result = sum_valid_numbers(sequence)
