import random

def generate_sequence(n):
    sequence = [0, 1]
    for i in range(2, n):
        sequence.append(sequence[i-1] + sequence[i-2])
    return sequence

def replace_every_third(sequence):
    for i in range(2, len(sequence), 3):
        sequence[i] = random.randint(1, 100)
    return sequence

def sum_sequence(sequence):
    return sum(sequence)

# Generate the first 100 numbers in the sequence
sequence = generate_sequence(100)

# Replace every third number with a random number
sequence = replace_every_third(sequence)

# Sum the sequence, excluding the replaced numbers
total_sum = sum_sequence(sequence)

total_sum
