def fibonacci_sum_even(n: int) -> int:
    a, b = 1, 1
    even_sum = 0
    while a <= n:
        if a % 2 == 0:
            even_sum += a
        a, b = b, a + b
    return even_sum

# Test with n = 10
fibonacci_sum_even(10)

def sum_even_fibonacci(n):
    a, b = 1, 1
    sum_even = 0
    while a <= n:
        if a % 2 == 0:
            sum_even += a
        a, b = b, a + b
    return sum_even

# Solve for n = 1000000
sum_even_fibonacci(1000000)
