def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    w = 2
    while i * i <= n:
        if n % i == 0:
            return False
        i += w
        w = 6 - w
    return True

def find_prime_quintuplet(start):
    x = start
    while True:
        if is_prime(x) and is_prime(x+1) and is_prime(x+2) and is_prime(x+3) and is_prime(x+4):
            return x
        x += 1

# Find the smallest prime quintuplet where x > 100,000
answer = find_prime_quintuplet(100001)
