def compute_final_number(operations):
    number = 0
    for op in operations:
        if op == 'D':
            number *= 2
        elif op == 'T':
            number *= 3
        elif op == 'I':
            number += 1
    return number

# Test the function with the example
print(compute_final_number('DTDII'))  # Output should be 2

