def find_and_sum_digits(s):
    for i in range(len(s) - 2):
        if s[i].isdigit() and s[i+1].isdigit() and s[i+2].isdigit():
            return int(s[i]) + int(s[i+1]) + int(s[i+2])
    return -1  # Return -1 if no sequence of three consecutive digits is found

# Example usage
example_string = "abc123def456ghi"
result = find_and_sum_digits(example_string)
print(result)  # Output should be 6

