def find_first_three_digit_sum(s):
    # Iterate through the string to find the first sequence of three consecutive digits
    for i in range(len(s) - 2):
        if s[i].isdigit() and s[i+1].isdigit() and s[i+2].isdigit():
            # Compute the sum of these three digits
            return int(s[i]) + int(s[i+1]) + int(s[i+2])
    return None  # Return None if no such sequence is found

