def longest_subarray_with_zero_sum(arr):
    sum_indices = {} 
    max_length = 0
    cumulative_sum = 0

    for i, num in enumerate(arr):
        cumulative_sum += num

        if cumulative_sum == 0:
            max_length = i + 1
        elif cumulative_sum in sum_indices:
            max_length = max(max_length, i - sum_indices[cumulative_sum])
        else:
            sum_indices[cumulative_sum] = i

    return max_length

arr = [1, 2, -2, 4, -4, 0, 1, 2, -3, 3]
answer = longest_subarray_with_zero_sum(arr)
print(answer)
