def longest_zero_sum_subarray(arr):
    prefix_sums = {0: -1}
    max_length = 0
    current_sum = 0
    for i, num in enumerate(arr):
        current_sum += num
        if current_sum in prefix_sums:
            max_length = max(max_length, i - prefix_sums[current_sum])
        else:
            prefix_sums[current_sum] = i
    return max_length

# Example usage:
arr = [1, 2, -3, 3, -2, 1, 2, -3, 3, -2]
print(longest_zero_sum_subarray(arr))  # Output should be 5
