def decode_and_evaluate(encoded_str):
    decoded_str = encoded_str.replace('A', '+').replace('B', '-').replace('C', '*').replace('D', '/')
    try:
        result = eval(decoded_str)
        return int(result) if result == int(result) else result
    except:
        return None

