def modified_fibonacci(n):
    if n == 0:
        return 0
    elif n == 1:
        return 1

    a, b = 0, 1
    for i in range(2, n + 1):
        if i % 3 == 0:
            c = - (a + b)
        else:
            c = a + b
        a, b = b, c
    return b

answer = modified_fibonacci(19)

