def is_vowel(char):
    return char in 'aeiou'

def is_consonant(char):
    return char.isalpha() and not is_vowel(char)

def sum_digits_in_sequence(sequence):
    total_sum = 0
    for s in sequence:
        for i in range(1, len(s) - 1):
            if s[i].isdigit() and is_vowel(s[i-1]) and is_consonant(s[i+1]):
                total_sum += int(s[i])
    return total_sum

