def mysterious_function(n):
    s = str(n)
    t = ""
    for i, c in enumerate(s):
        digit = ord(c) - ord('0')
        if i % 2 == 0:
            new_digit = (digit + 5) % 10
        else:
            new_digit = (digit * 2 + 13) % 10
        t += chr(new_digit + ord('0'))
    total = 0
    for i, c in enumerate(t):
        total += (ord(c) - ord('0')) * (i + 1)
    return total

answer = mysterious_function(9876543210)
print(answer)

