def sum_of_digits(n):
    return sum(int(digit) for digit in str(n))

def find_10th_term():
    term = 1
    for _ in range(9):  # We need the 10th term, so 9 iterations
        term = sum_of_digits(term) * 3
    return term

find_10th_term()

