# Define the function to process the list

def process_list(numbers):
    result = numbers[0]  # Start with the first number
    for num in numbers[1:]:
        if num % 2 == 0:
            result += num  # Add if even
        if num % 2 != 0:
            result *= num  # Multiply if odd
        if num % 5 == 0:
            result -= num  # Subtract if multiple of 5
    return result

# List to process
numbers = [4, 7, 10, 6, 15]

# Get the final result
final_result = process_list(numbers)
final_result
