# This Python code calculates a(20) for the given recurrence.
def solve():
    def a(n, memo={}):
        if n <= 1:
            return n
        if n not in memo:
            memo[n] = a(n-1, memo) + 2*a(n-2, memo)
        return memo[n]
    return a(20)

if __name__ == '__main__':
    print(solve())

