def smallest_multiple(n):
    from math import gcd
    def lcm(a, b):
        return a * b // gcd(a, b)
    multiple = 1
    for i in range(1, n+1):
        multiple = lcm(multiple, i)
    return multiple

print(smallest_multiple(20))

