def modified_fibonacci(n):
    if n == 1 or n == 2:
        return 1
    f = [0] * (n + 1)
    f[1], f[2] = 1, 1
    for i in range(3, n + 1):
        if i % 2 == 0:
            f[i] = f[i-1] + f[i-2] + 1
        else:
            f[i] = f[i-1] + f[i-2]
    return f[n] % 100

# Calculate for N = 10
result = modified_fibonacci(10)
print(result)  # Expected output: 76

def modified_fib(n):
    if n == 1 or n == 2:
        return 1
    a, b = 1, 1
    for i in range(3, n + 1):
        if i % 2 == 0:
            c = b + a + 1
        else:
            c = b + a
        a, b = b, c
    return b

# Calculate F(10) and take modulo 100
result = modified_fib(10) % 100
print(result)  # Expected output: 76
