count = 0
import math
for a in range(1, 51):
    for b in range(a, 51):
        for c in range(b, 51):
            if a + b + c > 50:
                continue
            if a + b <= c:
                continue
            A = a + b + c
            B = b + c - a
            C = a + c - b
            D = a + b - c
            E_num = A * B * C * D
            if E_num <= 0:
                continue
            root = math.isqrt(E_num)
            if root * root == E_num and root % 4 == 0:
                count += 1
print(count)
