count = 0
for a in range(1, 51):
    for b in range(a, 51):
        for c in range(b, 51):
            per = a + b + c
            if per > 50:
                continue
            # Using 16*area^2 = p(p-2a)(p-2b)(p-2c)
            area2 = per * (per - 2*a) * (per - 2*b) * (per - 2*c)
            if area2 <= 0:
                continue
            r = math.isqrt(area2)
            # Check perfect square and divisible by 4 (so area integer)
            if r * r == area2 and r % 4 == 0:
                count += 1
print(count)

