# Define helper functions
def digit_sum(n):
    return sum(int(d) for d in str(n))

def is_prime(k):
    if k < 2:
        return False
    if k == 2:
        return True
    if k % 2 == 0:
        return False
    for i in range(3, int(k**0.5) + 1, 2):
        if k % i == 0:
            return False
    return True

# Search for the smallest n
n = 1
while n < 100000:
    ds_n = digit_sum(n)
    ds_n2 = digit_sum(n * n)
    if ds_n == ds_n2 and is_prime(ds_n):
        print(n)
        break
    n += 1

