def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def solve():
    f_prev2 = 0  # f(0)
    f_prev1 = 1  # f(1)

    if 100 == 0:
        return 0
    elif 100 == 1:
        return 1

    for n in range(2, 101):
        f_n = f_prev1 + f_prev2 + gcd(n, f_prev1)
        f_prev2 = f_prev1
        f_prev1 = f_n

    return f_prev1 % 10007

solve()
