fact = [1] * 10
for i in range(1, 10):
    fact[i] = fact[i-1] * i

total = 0
for num in range(1, 2540161):
    n = num
    s = 0
    while n:
        s += fact[n % 10]
        n //= 10
    if s == num:
        total += num
print(total)

factorials = [1, 1, 2, 6, 24, 120, 720, 5040, 40320, 362880]

total = 0
for n in range(1, 2540161):
    s = 0
    for digit in str(n):
        s += factorials[int(digit)]
    if s == n:
        total += n

print(total)
