import math

MOD = 1000000007
n_target = 1000000

if n_target == 0:
    print(0)
elif n_target == 1:
    print(1)
else:
    a, b = 0, 1
    for n in range(2, n_target + 1):
        root = math.isqrt(n)
        if root * root == n:
            c = (b * a) % MOD
        else:
            c = (b + a) % MOD
        a, b = b, c
    print(b)

