def compute_total(s):
    total = 0
    vowels = "aeiou"
    for i, char in enumerate(s, start=1):
        if char.lower() in vowels:
            total += i * 3
        elif char.isalpha():
            total += i * 1
        elif char.isdigit():
            num = int(char)
            total += (num ** 2) * i
    return total

s = "Contender123"
result = compute_total(s)
print(result)

