def calculate_string_total(s):
    vowels = {'a', 'e', 'i', 'o', 'u'}
    total = 0

    for position, char in enumerate(s, start=1):
        if char.isalpha():
            char_lower = char.lower()
            if char_lower in vowels:
                total += position * 3
            else:
                total += position * 1
        elif char.isdigit():
            digit = int(char)
            total += (digit ** 2) * position
        # Non-alphanumeric characters are ignored

    return total

# Test with the given string
result = calculate_string_total('Contender123')
print(f"Total for 'Contender123': {result}")
