def special_fib(n):
    MOD = 1000000007
    a, b = 0, 1
    for i in range(n):
        if i % 3 == 0:
            a, b = b, (a + b) % MOD
        elif i % 3 == 1:
            a, b = b, (a * b) % MOD
        else:
            a, b = b, (a + b) % MOD
    return a

result = special_fib(100)
print(result)
